sources.sass\\components\\_main.scss:
.section {
    background-color: $header-text-color;
    padding-top: 20px;
    padding-bottom: 20px;

    // @media screen and (min-width: 768px) {
    //     padding-top: 60px;
    //     padding-bottom: 60px;
    // }
}

.container {
    padding: 0 20px;
    margin: 0 auto;

    @media screen and (min-width: 320px) {
        width: 320px;
        margin: 0 auto;
    }

    @media screen and (min-width: 768px) {
        display: flex;
        flex-wrap: wrap;
        width: 768px;
        padding: 0 32px;
    }

    @media screen and (min-width: 1024px) {
        width: 1024px;
    }
}

.main__button-list {
    display: flex;
    margin: 0 auto 20px;

    @media screen and (min-width: 768px) {
        margin: 0 auto 32px;
    }
}

.main__button-item:first-child {
    margin-right: 20px;

    @media screen and (min-width: 768px) {
        margin-right: 32px;
    }

    @media screen and (min-width: 1024px) {
        margin-right: 16px;
    }
}

.main__button {

    width: 130px;
    height: 44px;
    border-radius: 5px;
    padding: 13px 15px;

    text-transform: uppercase;
    font-size: 12px;
    line-height: 1.33;
    font-weight: 500;

    border: 1px solid $main-text-color;
    background-color: $header-text-color;


    @include respond-to (tablet) {
        width: 152px;
    }

    @include respond-to (desktop) {
        width: 147px;
        height: 42px;
    }

    @include transit(background-color, color, border-color);

    &:hover,
    &:focus {
        background-color: $accent-color;
        color: $header-text-color;
        border-color: $accent-color;
    }
}

.main__movie-card-list {
    width: 100%;

    @media screen and (min-width: 768px) {
        display: flex;
        flex-wrap: wrap;
        margin-right: calc(-1 * 32px);
    }

    @media screen and (min-width: 1024px) {
        margin-right: calc(-1 * 16px);
    }
}

.main__movie-card-item {
    width: 100%;

    @media screen and (min-width: 768px) {
        width: calc((100% - 32px * 2) / 2);
        margin-right: 16px;
        margin-left: 16px;
    }

    @media screen and (min-width: 1024px) {
        width: calc((100% - 16px * 3) / 3);
        margin-right: 8px;
        margin-left: 8px;
    }

    &:not(:last-child) {
        margin-bottom: 20px;
        
        @media screen and (min-width: 768px) {
            margin-bottom: 0;
        }
    }

    &:not(:nth-last-child(-n+2)) {
        @media screen and (min-width: 768px) {
            margin-bottom: 32px;
        }
    
        @media screen and (min-width: 1024px) {
            margin-bottom: 0;
        }
    }

    &:not(:nth-last-child(-n+3)) {
        @media screen and (min-width: 1024px) {
            margin-bottom: 32px;
        }
    }
}

.main__movie-img {
    margin-bottom: 10px;
    width: 100%;
    border-radius: 5px;

    @media screen and (min-width: 1024px) {
        margin-bottom: 8px;
    }
}

.main__movie-title {
    text-transform: uppercase;
}

.main__movie-title,
.main__movie-genre {
    color: $main-text-color;
    font-weight: 500;
    font-size: 12px;
    line-height: 1.33;
}

.main__movie-genre {
    display: inline;
    color: $accent-color;
}

.main__movie-year::before {
    content: '';
    width: 1px;
    height: 10px;
    margin: 0 3px;
    display: inline-block;
    background-color: $accent-color;
}

.main__movie-raiting {
    // Для вкладки MyLibrary:
    // display: inline; 
    display: none;
    margin-left: 8px;
    padding-right: 9px;
    padding-left: 9px;
    color: $header-text-color;
    background-color: $accent-color;
    border-radius: 5px;
    font-size: 12px;
    line-height: 1.16;
    font-weight: 500;
}

sources.sass\\utils\\_variables.scss:
$border-radius: 5px;

// colors
$accent-color: #ff6b08;
$second-accent-color: #ff6b01;
$header-text-color: #ffffff;
$main-text-color: #000000;
$second-text-color: #8C8C8C;
$footer-text-color: #545454;
$footer-bg-color: #F7F7F7;
$error-text-color: #FF001B;

// mobile gaps
$mobile-header-links-gap: 40px;
$mobile-films-vertical-gap: 20px;
$mobile-pagination-top-bottom-margin: 40px;
$mobile-buttons-gap: 20px;
$mobile-modal-buttons-gap: 20px;

// tablet gaps
$tablet-films-vertical-gap: 32px;
$tablet-pagination-top-bottom-margin: 40px;
$tablet-buttons-gap: 20px;

// desktop gaps
$desktop-pagination-top-bottom-margin: 60px;
$desktop-buttons-gap: 16px;
$desktop-modal-buttons-gap: 16px;

// backdrop
$backdrop-color: #000000;
$backdrop-opacity: 0.25;

sources.sass\\components\\_details.scss:
.details{
    &__backdrop{
        display: flex;
        justify-content: center;

        position: fixed;
        width: 100%;
        height: 100vh;

        left: 0px;
        top: 0px;
        z-index: 1;
        transition: 0.5s linear;

        overflow-y: scroll;
      
        // background-image: url(https://image.tmdb.org/t/p/original/rlCRM7U5g2hcU1O8ylGcqsMYHIP.jpg);
        background-repeat: no-repeat;
        background-size: cover;
    }

    &__modal{
        display: block;
        position: absolute;
        width: 90%;

        top: 20px;

        z-index: 2;
        
        padding: 48px 20px 40px;

        background-color: rgb(255, 255, 255);
    
 
        @include respond-to (tablet){
            top: 219px;
            width: 704px;

            padding: 40px 72px 40px 36px;
        }

        @include respond-to (desktop){
            top: 151px;
            width: 814px;

            padding: 40px 16px;
        }
    }

    &__close-button{
    position: absolute;
        @include centered;

        top: 8px;
        right: 8px;
    
        min-width: 30px;
        min-height: 30px;
        padding: 0;

        background-color: $header-text-color;

        &:hover,&:focus{
            fill: $accent-color;
        }
    }

    &__box{
        display: block;
        margin: 0 auto;
        width: 240px;

        @include respond-to (tablet){
            display: flex;
            justify-content: center;

            width: 100%;
        }
        
    }

    &__poster{
        display: block;
        margin-bottom: 20px;

        @include respond-to (tablet){
            margin-right: 68px;
        }
        @include respond-to (desktop) {
            margin-right: 16px;
            margin-bottom: 0;
        }   
    }

    &__image{
        display: block;
        border-radius: 5px;
        width: 100%;

        @include respond-to (tablet) {
            width: 264px;
            height: 374px;
        }
        @include respond-to (desktop){
            width: 375px;
            height: 478px;
        }    
        }

    &__info{
        display: block;

        @include respond-to (tablet){
            width: 264px;
        }

        @include respond-to (desktop){
            width: 391px;
        }
    }

    &__title{
        margin-bottom: 20px;

        font-size: 20px;
        line-height: 1.15;
        font-weight: 500;

        text-transform: uppercase;

        @include respond-to (desktop){
            font-size: 30px;
            line-height: 1.17;
        }
    }
    
    &__attributesList li:not(:last-child){
        margin-bottom: 8px;
    }

    &__attributesList {
        margin-bottom: 20px;
    }

    &__attribute{
        display: inline-block;
        width: 152px;

        color: $second-text-color;
        font-size: 12px;
        line-height: 1.33;
        font-weight: 500;

        &-value{
        
            font-size: 12px;
            line-height: 1.17;
            font-weight: 500;
        }

        &-vote{
            display: inline-block;
            width: 36px;
            height: 16px;

            background-color: $accent-color;
            border-radius: 5px;
            color: #fff;
            line-height: 1.33;
            text-align: center;//выровнять вертикаль   
        }

        &-title{
            text-transform: uppercase;
        }
    }

    &__subtitle{
        margin-bottom: 8px;

        text-transform: uppercase;
        font-size: 12px;
        line-height: 1.33;
        font-weight: 500;
    }

    &__description{
        margin-bottom: 20px;

        font-size:12px;
        line-height: 1.67;
        font-weight: 500;

        @include respond-to (tablet) {
            margin-bottom: 36px;
            }
        
        @include respond-to (desktop) {
                margin-bottom: 20px;
            }      
    }

    &__buttonSet{
        display: flex;
    }
}

.details__button{
   
    width: 110px;
    height: 44px;
    border-radius: 5px;
    padding: 6px 10px 6px;
  
    text-transform: uppercase;
    font-size: 12px;
    line-height: 1.33;
    font-weight: 500;

    border: 1px solid $main-text-color;
    background-color: $header-text-color;


    @include respond-to (tablet){
        width: 125px;
    }

    @include respond-to (desktop){
        width: 136px;
    }

    @include transit(background-color, color, border-color);

    &:hover,
    &:focus {
        background-color: $accent-color;
        color: $header-text-color;
        border-color: $accent-color;
    }  
}

.details__button:not(:last-child){

    margin-right: 20px;

    @include respond-to (tablet){
        margin-right: 15px;
    }

    @include transit(background-color, color, border);
  
}
  
.isHidden {
    position: absolute;
    width: 1px;
    height: 1px;
    margin: -1px;
    border: 0;
    padding: 0;
    clip: rect(0 0 0 0);
    overflow: hidden;
}

sources.sass\\components\\_loader.scss:
.loader {
display: none;

    color: $accent-color;
    font-size: 90px;
    text-indent: -9999em;
    overflow: hidden;
    width: 1em;
    height: 1em;
    border-radius: 50%;
    margin: 72px auto;
    position: relative;
    -webkit-transform: translateZ(0);
    -ms-transform: translateZ(0);
    transform: translateZ(0);
    -webkit-animation: load6 1.7s infinite ease, round 1.7s infinite ease;
    animation: load6 1.7s infinite ease, round 1.7s infinite ease;
}

@-webkit-keyframes load6 {
    0% {
        box-shadow: 0 -0.83em 0 -0.4em, 0 -0.83em 0 -0.42em, 0 -0.83em 0 -0.44em, 0 -0.83em 0 -0.46em, 0 -0.83em 0 -0.477em;
    }

    5%,
    95% {
        box-shadow: 0 -0.83em 0 -0.4em, 0 -0.83em 0 -0.42em, 0 -0.83em 0 -0.44em, 0 -0.83em 0 -0.46em, 0 -0.83em 0 -0.477em;
    }

    10%,
    59% {
        box-shadow: 0 -0.83em 0 -0.4em, -0.087em -0.825em 0 -0.42em, -0.173em -0.812em 0 -0.44em, -0.256em -0.789em 0 -0.46em, -0.297em -0.775em 0 -0.477em;
    }

    20% {
        box-shadow: 0 -0.83em 0 -0.4em, -0.338em -0.758em 0 -0.42em, -0.555em -0.617em 0 -0.44em, -0.671em -0.488em 0 -0.46em, -0.749em -0.34em 0 -0.477em;
    }

    38% {
        box-shadow: 0 -0.83em 0 -0.4em, -0.377em -0.74em 0 -0.42em, -0.645em -0.522em 0 -0.44em, -0.775em -0.297em 0 -0.46em, -0.82em -0.09em 0 -0.477em;
    }

    100% {
        box-shadow: 0 -0.83em 0 -0.4em, 0 -0.83em 0 -0.42em, 0 -0.83em 0 -0.44em, 0 -0.83em 0 -0.46em, 0 -0.83em 0 -0.477em;
    }
}

@keyframes load6 {
    0% {
        box-shadow: 0 -0.83em 0 -0.4em, 0 -0.83em 0 -0.42em, 0 -0.83em 0 -0.44em, 0 -0.83em 0 -0.46em, 0 -0.83em 0 -0.477em;
    }

    5%,
    95% {
        box-shadow: 0 -0.83em 0 -0.4em, 0 -0.83em 0 -0.42em, 0 -0.83em 0 -0.44em, 0 -0.83em 0 -0.46em, 0 -0.83em 0 -0.477em;
    }

    10%,
    59% {
        box-shadow: 0 -0.83em 0 -0.4em, -0.087em -0.825em 0 -0.42em, -0.173em -0.812em 0 -0.44em, -0.256em -0.789em 0 -0.46em, -0.297em -0.775em 0 -0.477em;
    }

    20% {
        box-shadow: 0 -0.83em 0 -0.4em, -0.338em -0.758em 0 -0.42em, -0.555em -0.617em 0 -0.44em, -0.671em -0.488em 0 -0.46em, -0.749em -0.34em 0 -0.477em;
    }

    38% {
        box-shadow: 0 -0.83em 0 -0.4em, -0.377em -0.74em 0 -0.42em, -0.645em -0.522em 0 -0.44em, -0.775em -0.297em 0 -0.46em, -0.82em -0.09em 0 -0.477em;
    }

    100% {
        box-shadow: 0 -0.83em 0 -0.4em, 0 -0.83em 0 -0.42em, 0 -0.83em 0 -0.44em, 0 -0.83em 0 -0.46em, 0 -0.83em 0 -0.477em;
    }
}

@-webkit-keyframes round {
    0% {
        -webkit-transform: rotate(0deg);
        transform: rotate(0deg);
    }

    100% {
        -webkit-transform: rotate(360deg);
        transform: rotate(360deg);
    }
}

@keyframes round {
    0% {
        -webkit-transform: rotate(0deg);
        transform: rotate(0deg);
    }

    100% {
        -webkit-transform: rotate(360deg);
        transform: rotate(360deg);
    }
}

