sources.sass\\components\\_slider.scss:
.slider-section{
    margin-top: 15px;
    margin-bottom: 15px;
    position: absolute;
        top: -9999px;
        left: -9999px;
}


.wrapper {
    width: 100%;
    margin: 0 auto;
    position: relative;
}


/* slider */
.slider-container {
    width: 100%;
    overflow: hidden;
}

.slider-track {
    display: flex;
    transition: 200ms;
}

.slider-item {
    min-width: 124px;

    &:hover,
    &:focus {
        cursor: pointer;
    }
}

@media screen and (min-width: 768px) {
    .slider-item {
            min-width: 145px;
        }
}

@media screen and (min-width: 1024px) {
    .slider-item {
        min-width: 150px;
    }
}

.slider-item:not(last-child) {
margin-right: 10px;
}

.slider-images {
    border-radius: $border-radius;
}

.slider-headline {
    margin: 0 auto 10px;
}

.slider-btns {
    background-color: transparent;
    font-size: 40px;
    color: $second-text-color;
    transition: 150ms;
    opacity: 50%;


    &:hover {
        opacity: 1;
        color: $accent-color;
    }
}

.btn-prev {
position: absolute;
left: -30;
top: 50%;
transform: translateY(-50%);
}

.btn-next {
    position: absolute;
    right: -30;
        top: 50%;
        transform: translateY(-50%);
}

sources.sass\\components\\_themeSwitcher.scss:
body.light-theme {
  background-color: #fff;
}

.light-on {

  
background-color: rgb(20, 208, 64);
}

.dark-on {
background-color: rgb(136, 62, 27);

color: #fff;


}

body.dark-theme {

background-image: linear-gradient(to right, rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
url("../images/home-pc-x2.jpg");
  color: #fff;
  background-size: cover;
  
  width: 100%;
}


/* The switch - the box around the slider */
.switch {
  position: relative;
  display: inline-block;
  width: 50px;
  height: 24px;
 
}

/* Hide default HTML checkbox */
.switch input {display:none;}

/* The slider */
.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #ccc;
  -webkit-transition: .4s;
  transition: .4s;
}

.slider:before {
  position: absolute;
  content: "";
  height: 16px;
  width: 16px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  -webkit-transition: .4s;
  transition: .4s;
}

input:checked + .slider {
  background-color: #333;
}

input:focus + .slider {
  box-shadow: 0 0 1px #ccc;
}

input:checked + .slider:before {
  -webkit-transform: translateX(26px);
  -ms-transform: translateX(26px);
  transform: translateX(26px);
}

/* Rounded sliders */
.slider.round {
  border-radius: 34px;
}

.slider.round:before {
  border-radius: 50%;
}

