sources.sass\\components\\_footer-modal.scss:
.footer-backdrop {
    position: fixed;
    left: 0px;
    top: 0px;
    width: 100vw;
    height: 100vh;
    background: rgba(0, 0, 0, 0.8);
    overflow-y: scroll;
    z-index: 2;
}

.is-hidden {
    opacity: 0;
    pointer-events: none;
    visibility: hidden;
}

.footer-modal {
    min-height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 30px 10px;

    &__container {
        position: relative;
        width: 320px;
        // background-color: rgba(54, 54, 54, 0.8);
        // background: rgba(66, 66, 66, 0.7);    
        padding: 30px;

        @include respond-to (tablet) {
            width: 720px;
            padding: 50px;
        }

        @include respond-to (desktop) {
            width: 900px;
        }
    }

    &__close {
        position: absolute;
        top: 4px;
        right: 4px;
        @include centered;

        min-width: 30px;
        min-height: 30px;
        padding: 0;

        background-color: transparent;
        border: none;
    }

    &__close--svg {
        fill: #545454;

        :hover,
        :focus {
            fill: #ff6b08;
            width: 20px;
        }
    }

    &__title {
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 500;
        font-size: 20px;
        color: #fff;
        text-align: center;
        margin-bottom: 25px;
    }


    &__list {
        @include centered;
        flex-wrap: wrap;
    }

    &__item {
        position: relative;
        margin: 0 10px;
        width: 250px;
        height: 250px;
        
        border-radius: 30px; 
       
        overflow: hidden;
        @include respond-to(mobile-only){
            width: 250px;
            height: 250px;
        }

        @include respond-to (tablet) {
                width: 185px;
                height: 185px;
            }
        
        @include respond-to (tablet-only){
            &:not(:last-child) {
                margin-bottom: 20px;
            }
        }

        @include respond-to (desktop) {
            &:nth-child(-n+6) {
                margin-bottom: 20px;
            }
        }
    }
       
    &__item--container{
        position: relative;

        width: 100%;
        height: 100%;
        
        // z-index: 5;
        display: inline-block;
        overflow: hidden;
       
        text-align: center;
        // cursor: pointer;
        border-radius: 50%;

        &:hover{
            border-radius: 30px;
            background-color: rgba(0, 0, 0, 0.8);
            transition: 1s
            
        }
        &:hover .footer-modal__description {
            opacity: 1;
            transform: translate3d(0, 0, 0);
            z-index: 6;

        }
        &:hover .footer-modal__description h3::after {
        transform: translate3d(0, 0, 0);
        opacity: 1;
        z-index: 6;
        }
        
    }
    

    &__item--img {
        margin-bottom: 7px;
        // z-index: 5;
        width: 100%;
        min-height: 100%;
        transition: 1s;
        transform: scale(1.1);
        // backface-visibility: hidden;
        &:hover{
            opacity: 0.8;
        }
    }

     &__item--img:hover {
            transform: scale(1.2);
            border-radius: 30px;
          
            z-index: 5;
         }
  
    &__description {
              position: absolute;
              top: 0;
              left: 0;
              padding: 1em 0;
              opacity: 0;
              background-color: rgba(54, 54, 54, 0.8);
                          
              width: 100%;
              height: 100%;
              transition: opacity 0.35s, transform 0.35s;
              transform: translate3d(100%, 0, 0);
            &::before,
            &::after{
                pointer-events: none;
            }

          }
    &__description p {
        position: absolute;
         
        width: 100%;   
        color: #ff6b08;
        font-family: 'Roboto';
        font-style: italic;
        font-weight: 500;
        font-size: 14px;
        line-height: 1.33;

        @include respond-to(mobile-only) {
            top: 110px;
            }
        
        @include respond-to (tablet) {
            top: 90px;
            }
    }
        
    &__description h3 {
        position: absolute;
       
        width: 100%;

        color: #fff;
        font-family: 'Roboto';
        font-style: normal;
        font-weight: 500;
        font-size: 16px;
        line-height: 1.33;
        
        @include respond-to(mobile-only) {
             top: 80px;
            }
        
        @include respond-to (tablet) {
            top: 38px;
            }

         &::after{
            position: absolute;
            
            width: 80%;
            height: 3px;
            background-color: #fff;
            content: '';
            transition: transform 0.6s;
            transform: translate3d(-100%, 0, 0);

            @include respond-to(mobile-only) {
                top: 55px;
                left: 25px;
                }
            
            @include respond-to (tablet) {
                    top: 43px;
                    left: 18px;
                }

    }
}
   
    &__social-links {
        @include centered;
        margin-bottom: 15px;
        position: absolute;
        width: 100%;

        @include respond-to(mobile-only) {
                top: 160px;
                right: 0;
            }
        
        @include respond-to (tablet) {
            top: 117px;
            left: 6px;
            }
    }

    &__social-link {
        z-index: 99;
        margin-right: 15px;
        cursor: pointer;
            
        width: 40px;
        height: 40px;
    }

    &__social-link--svg {
    fill: #fff;
        width: 40px;
        height: 40px;
        @include centered;
    }

    &__social-link--svg.linkedin{
        width: 43px;
        height: 43px;
        border-radius: 50%;
    }

    &__social-link--svg:hover,
    &__social-link--svg:focus {
        fill: #ff6b08;
    }
}
